var additionalList = document.querySelector('.additional-list');
var menuButton = document.querySelector('.category-btn');

menuButton.addEventListener('click', function() {
  if (additionalList.classList.contains('additional-list-show')) {
    additionalList.classList.remove('additional-list-show');
  } else {
    additionalList.classList.add('additional-list-show');    
  }
});
